    }
    setState([state[1], newIdx, direction])
  }

  //render

  const anchorClasses = classNames(
    `carousel-control-${direction}`, className,
  )

  return (
    <a
      className={anchorClasses}
      {...attributes}
      onClick={onClick}
      ref={innerRef}
    >
      { children || <span 
        className={`carousel-control-${direction}-icon`} 
        aria-label={direction} 
      />}
    </a>
  )
