import React, { useState } from "react";
import {CCard, CButton, CCollapse, CCardBody, CCardFooter} from '@coreui/react';

export default () => {
  /*example start: script*/
  const [collapse, setCollapse] = useState(false);
  const [collapse2, setCollapse2] = useState(false);

  const onEntering = () => {};
  const onEntered = () => {};
  const onExiting = () => {};
  const onExited = () => {};

  const toggle = (e)=>{
    setCollapse(!collapse);
    e.preventDefault();
  }

  // inner
  const toggle2 = (e)=>{
    setCollapse2(!collapse2);
    e.preventDefault();
  }

  return (
    <CCard>
      <CCollapse
        show={collapse}
        onEntering={onEntering}
        onEntered={onEntered}
        onExiting={onExiting}
        onExited={onExited}
      >
        <CCardBody>
          <p>
            Anim pariatur cliche reprehenderit,
            enim eiusmod high life accusamus terry richardson ad squid. Nihil
            anim keffiyeh helvetica, craft beer labore wes anderson cred
            nesciunt sapiente ea proident.
          </p>

          <CCard>
            <CCollapse
              show={collapse2}
            >
              <CCardBody>
                <p>
                  Anim pariatur cliche reprehenderit,
                  enim eiusmod high life accusamus terry richardson ad squid. Nihil
                  anim keffiyeh helvetica, craft beer labore wes anderson cred
                  nesciunt sapiente ea proident.
                </p>
              </CCardBody>
            </CCollapse>
            <CCardFooter>
              <CButton
                onClick={toggle2}
                className={'mb-1'}
              >
                Toggle Inner Collapse
              </CButton>
            </CCardFooter>
          </CCard>

        </CCardBody>
      </CCollapse>
      <CCardFooter>
        <CButton
          color="primary"
          onClick={toggle}
          className={'mb-1'}
        >
          Toggle Collapse
        </CButton>
      </CCardFooter>
    </CCard>
  )
  /*example end: script*/
}

