import React, { useState, useEffect, useRef, useMemo } from 'react';
import PropTypes from 'prop-types';
import Chart from 'chart.js';
import { customTooltips as cuiCustomTooltips } from '@coreui/chartjs';
import '@coreui/chartjs/dist/css/coreui-chartjs.css';

//component - CoreUI / CCharts

const CCharts = props => {

  const {
    //
    innerRef,
    datasets,
    labels,
    options,
    plugins,
    type,
    ...attributes
  } = props;

  //console.log(Chart);

  const compData = useRef({ firstRun: true }).current;
  const [chart, setChart] = useState();
  const ref = useRef();

  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  // const type = ''

  // methods

  const renderChart = () => {
    destroyChart();
    setChart(new Chart(
      ref.current.getContext('2d'),
      chartConfig
    ));
  };
  const updateChart = () => {
    Object.assign(chart, chartConfig);
    chart.update();
  };
  const destroyChart = () => {
    if (chart) {
      chart.destroy();
    }
  };

  // vars

  const _uid = '';
  const safeId = (() => {
    // as long as this._uid() works there is no need to generate the key
    const key = () => Math.random().toString(36).replace('0.', '');
    return '__safe_id__' + (_uid || key());
  })();
  const computedDatasets = (() => {
    return datasets;
  })();
  //
  const computedLabels = (() => {
    if (labels && typeof labels !== 'string') {
      return labels;
    } else if (!datasets || !datasets[0] || !datasets[0].data) {
      return [];
    }
    const emptyLabels = Array(datasets[0].data.length).fill('');
    if (labels === 'indexes') {
      return emptyLabels.map((u, i) => i + 1);
    } else if (labels === 'months') {
      return emptyLabels.map((u, i) => months[i % 12]);
    }
    return emptyLabels;
  })();
  const computedData = (() => {
    return {
      datasets: computedDatasets,
      labels: computedLabels
    };
  })();
  const customTooltips = (() => {
    if (options && options.tooltips) {
      return;
    }
    return {
      tooltips: {
        enabled: false,
        custom: cuiCustomTooltips,
        intersect: true,
        mode: 'index',
        position: 'nearest',
        callbacks: {
          labelColor(tooltipItem, chart) {
            function getValue(prop) {
              return typeof prop === 'object' ? prop[tooltipItem.index] : prop;
            }

            const dataset = chart.data.datasets[tooltipItem.datasetIndex];
            //tooltipLabelColor is coreUI custom prop used only here
            const backgroundColor = getValue(
              dataset.tooltipLabelColor ||
              dataset.pointHoverBackgroundColor ||
              dataset.borderColor ||
              dataset.backgroundColor
            );
            return {
              backgroundColor
            };
          }
        }
      }
    };
  })();
  const computedOptions = (() => {
    return Object.assign({}, options, customTooltips || {});
  })();
  const chartConfig = (() => {
    return {
      type: type,
      data: computedData,
      options: computedOptions || options,
      plugins: plugins
    };
  })();

  //watch

  //chartConfig
  useMemo(() => {
    if (compData.firstRun) return;
    updateChart();
  }, [chartConfig]);

  // effect

  useEffect(() => {
    renderChart();
    compData.firstRun = false;
    return () => {
      destroyChart();
    };
  }, []);

  // render

  return (
    <div {...attributes} ref={innerRef}>
      <canvas id={safeId} ref={ref} />
    </div>
  );

};

CCharts.propTypes = {
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  datasets: PropTypes.array,
  labels: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
  options: PropTypes.object,
  plugins: PropTypes.array,
  type: PropTypes.string
};

CCharts.defaultProps = {};

export default CCharts;

import React, { useMemo, useState } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

const colog = () => {
  if (process && process.env && process.env.NODE_ENV === 'development') {
    console.log.apply(this);
  }
}

const toCamelCase = (str) => {
  return str.replace(/([-_][a-z0-9])/ig, ($1) => {
    return $1.toUpperCase()
  }).replaceAll('-', '')
}

//component - CoreUI / CIconRaw
const CIconRaw = props => {

  const {
    className,
    //
    name,
    content,
    customClasses,
    size,
    src,
    title,
    use,
    ...attributes
  } = props

  const [change, setChange] = useState(0)

  useMemo(() => setChange(change + 1), [name, JSON.stringify[content]])

  const iconName = useMemo(()=>{
    const iconNameIsKebabCase = name && name.includes('-');
    return iconNameIsKebabCase ? toCamelCase(name) : name
  }, [change])

  const titleCode = title ? `<title>${title}</title>` : ''

  const code = useMemo(() => {
    if (content) {
      return content
    } else if (name && React.icons) {
      return React.icons[iconName] ? React.icons[iconName] :
        colog('Not existing icon: '+ iconName + ' in React.icons object')
    }
  }, [change])

  const iconCode = useMemo(()=>{
    return Array.isArray(code) ? code[1] || code[0] : code
  }, [change])

  const scale = (()=>{
    return Array.isArray(code) && code.length > 1 ? code[0] : '64 64'
  })()

  const viewBox = (()=>{
    return attributes.viewBox || `0 0 ${scale}`
  })()

  const computedSize = (()=>{
    const addCustom = !size && (attributes.width || attributes.height);
    return size === 'custom' || addCustom ? 'custom-size' : size
  })()

  //render
  const computedClasses = classNames(
    'c-icon',
    computedSize && `c-icon-${computedSize}`,
    className
  )

  const classes = customClasses || computedClasses

  return (
    <React.Fragment>
      { !src && !use &&
        <svg
          {...attributes}
          xmlns="http://www.w3.org/2000/svg"
          viewBox={viewBox}
          className={classes}
          role="img"
          dangerouslySetInnerHTML={{__html: titleCode + iconCode}}
        />
      }
      { src && !use &&
        <img
          {...attributes}
          className={className}
          src={src}
          role="img"
        />
      }
      { !src && use &&
        <svg
          {...attributes}
          xmlns="http://www.w3.org/2000/svg"
          className={classes}
          role="img"
        >
          <use href={use}></use>
        </svg>
      }
    </React.Fragment>
  )
}

CIconRaw.propTypes = {
  className: PropTypes.string,
  //
  name: PropTypes.string,
  content: PropTypes.oneOfType([PropTypes.string, PropTypes.array]),
  size: PropTypes.oneOf([
    'custom', 'custom-size', 'sm', 'lg', 'xl',
    '2xl', '3xl', '4xl', '5xl', '6xl', '7xl', '8xl', '9xl'
  ]),
  customClasses: PropTypes.oneOfType([PropTypes.string, PropTypes.array, PropTypes.object]),
  src: PropTypes.string,
  title: PropTypes.string,
  use: PropTypes.string
};

export default CIconRaw

