import React, {useEffect, useRef} from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {tagPropType, mapToCssModules, omit, keyCodes} from './Shared/helper.js';
import {Manager} from 'react-popper';

export const Context = React.createContext({});

//component - CoreUI / CPopperContentWrapper

class CPopperContentWrapper extends React.Component {
  getChildContext(){
    return this.context;
  }
  render(){
    return this.props.children;
  }
}

CPopperContentWrapper.propTypes = {
  children: PropTypes.node
};

CPopperContentWrapper.contextType = Context;

CPopperContentWrapper.childContextTypes = {
  toggle: PropTypes.func.isRequired,
  isOpen: PropTypes.bool.isRequired,
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']).isRequired,
  inNavbar: PropTypes.bool.isRequired
};

//component - CoreUI / CDropdownCustom

const CDropdownCustom = props=>{

  const {
    className,
    cssModule,
    //
    innerRef,
    show,
    group,
    size,
    inNav,
    setActiveFromChild,
    active,
    addonType,
    ...attributes
  } = omit(props, ['toggle', 'disabled', 'inNavbar', 'direction']);

  const fields = useRef({
    firstRender: true,
    ref: {current: null}
  }).current;

  const reference = (r)=>{
    fields.ref.current = r;
    innerRef && innerRef(r);
  }

  const getContainer = ()=>{
    if (fields._$container) return fields._$container;
    fields._$container = ReactDOM.findDOMNode(fields.ref.current);
    return fields._$container;
  }

  const getMenuCtrl = ()=>{
    if (fields._$menuCtrl) return fields._$menuCtrl;
    fields._$menuCtrl = getContainer().querySelector('[aria-expanded]');
    return fields._$menuCtrl;
  }

  const getMenuItems = ()=>{
    return [].slice.call(getContainer().querySelectorAll('[role="menuitem"]'));
  }

  const addEvents = ()=>{
    ['click', 'touchstart', 'keyup'].forEach(event =>
      document.addEventListener(event, handleDocumentClick, true)
    );
    fields.handleDocumentClick = handleDocumentClick;
  }

  const removeEvents = ()=>{
    ['click', 'touchstart', 'keyup'].forEach(event =>
      document.removeEventListener(event, fields.handleDocumentClick, true)
    );
  }

  const handleDocumentClick = e=>{
    if (e && (e.which === 3 || (e.type === 'keyup' && e.which !== keyCodes.tab))) return;
    const container = getContainer();
    if (container.contains(e.target) && container !== e.target && (e.type !== 'keyup' || e.which === keyCodes.tab)) {
      return;
    }
    toggle(e);
  }

  const handleKeyDown = e=>{
    if (
      /input|textarea/i.test(e.target.tagName)
      || (keyCodes.tab === e.which && e.target.getAttribute('role') !== 'menuitem')
    ) {
      return;
    }
    e.preventDefault();
    if (fields.disabled) return;
    if (getMenuCtrl() === e.target) {
      if (
        !fields.isOpen
        && ([keyCodes.space, keyCodes.enter, keyCodes.up, keyCodes.down].indexOf(e.which) > -1)
      ) {
        toggle(e);
        setTimeout(()=>getMenuItems()[0].focus());
      }
    }
    if (fields.isOpen && (e.target.getAttribute('role') === 'menuitem')) {
      if ([keyCodes.tab, keyCodes.esc].indexOf(e.which) > -1) {
        toggle(e);
        getMenuCtrl().focus();
      } else if ([keyCodes.space, keyCodes.enter].indexOf(e.which) > -1) {
        e.target.click();
        getMenuCtrl().focus();
      } else if (
        [keyCodes.down, keyCodes.up].indexOf(e.which) > -1
        || ([keyCodes.n, keyCodes.p].indexOf(e.which) > -1 && e.ctrlKey)
      ) {
        const $menuitems = getMenuItems();
        let index = $menuitems.indexOf(e.target);
        if (keyCodes.up === e.which || (keyCodes.p === e.which && e.ctrlKey)) {
          index = index !== 0 ? index - 1 : $menuitems.length - 1;
        } else if (keyCodes.down === e.which || (keyCodes.n === e.which && e.ctrlKey)) {
          index = index === $menuitems.length - 1 ? 0 : index + 1;
        }
        $menuitems[index].focus();
      } else if (keyCodes.end === e.which) {
        const $menuitems = getMenuItems();
        $menuitems[$menuitems.length - 1].focus();
      } else if (keyCodes.home === e.which) {
        const $menuitems = getMenuItems();
        $menuitems[0].focus();
      } else if ((e.which >= 48) && (e.which <= 90)) {
        const $menuitems = getMenuItems();
        const charPressed = String.fromCharCode(e.which).toLowerCase();
        for (let i = 0; i < $menuitems.length; i += 1) {
          const firstLetter = $menuitems[i].textContent && $menuitems[i].textContent[0].toLowerCase();
          if (firstLetter === charPressed) {
            $menuitems[i].focus();
            break;
          }
        }
      }
    }
  }

  const handleProps = ()=>{
    if (props.show) {
      addEvents();
    } else {
      removeEvents();
    }
  }

  const toggle = e=>{
    if (fields.disabled) {
      return e && e.preventDefault();
    }
    return props.toggle(e);
  }

  //effect

  useEffect(() => {
    if (fields.firstRender){
      return
    }
    handleProps();
  }, [props.show]);

  useEffect(() => {
    fields.firstRender = false;
    handleProps();
    return function cleanup() {
      removeEvents();
    };
  }, []);

  //render

  fields.disabled = props.disabled;
  fields.isOpen = props.show;

  const direction = props.direction; //(props.direction === 'down' && dropup) ? 'up' :

  attributes.tag = attributes.tag || (inNav ? 'li' : 'div');

  let subItemIsActive = false;

  if (setActiveFromChild){
    React.Children.map(props.children[1].props.children,
      (dropdownItem) => {
        if (dropdownItem && dropdownItem.props.active) subItemIsActive = true;
      }
    );
  }

  const classes = mapToCssModules(classNames(
    className,
    direction !== 'down' && `drop${direction}`,
    inNav && active ? 'active' : false,
    setActiveFromChild && subItemIsActive ? 'active' : false,
    {
      [`input-group-${addonType}`]: addonType,
      'btn-group': group,
      [`btn-group-${size}`]: !!size,
      'dropdown': !group && !addonType,
      'show': show,
      'nav-item': inNav
    }
  ), cssModule);

  return (
    <Context.Provider value={{
      toggle: props.toggle,
      isOpen: props.show,
      direction: props.direction, //(props.direction === 'down' && props.dropup) ? 'up' :
      inNavbar: props.inNavbar,
    }}>
      <CPopperContentWrapper>
        <Manager {...attributes} className={classes} onKeyDown={handleKeyDown} ref={reference} />
      </CPopperContentWrapper>
    </Context.Provider>
  );

}

CDropdownCustom.propTypes = {
  tag: tagPropType,
  children: PropTypes.node,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']),
  group: PropTypes.bool,
  show: PropTypes.bool,
  disabled: PropTypes.bool,
  active: PropTypes.bool,
  addonType: PropTypes.oneOfType([PropTypes.bool, PropTypes.oneOf(['prepend', 'append'])]),
  size: PropTypes.string,
  toggle: PropTypes.func,
  inNav: PropTypes.bool,
  inNavbar: PropTypes.bool,
  setActiveFromChild: PropTypes.bool,
};

CDropdownCustom.defaultProps = {
  show: false,
  direction: 'down',
  active: false,
  addonType: false,
  inNav: false,
  inNavbar: false,
  setActiveFromChild: false,
  group: true
};

export default CDropdownCustom;

import React, {useContext} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {tagPropType, mapToCssModules} from './Shared/helper.js';
import {Popper} from 'react-popper';
import {Context} from './CDropdownCustom';

//component - CoreUI / CDropdownMenu

const CDropdownMenu = props=>{

  const {
    className,
    cssModule,
    right,
    tag,
    flip,
    modifiers,
    persist,
    innerRef,
    ...attributes
  } = props;

  const noFlipModifier = { flip: { enabled: false } };

  const directionPositionMap = {
    up: 'top',
    left: 'left',
    right: 'right',
    down: 'bottom',
  };

  const context = useContext(Context);

  //render

  const classes = mapToCssModules(classNames(
    className,
    'dropdown-menu',
    {
      'dropdown-menu-right': right,
      'show': context.isOpen,
    }
  ), cssModule);

  let Tag = tag;

  if (persist || (context.isOpen && !context.inNavbar)) {
    Tag = Popper;
    const position1 = directionPositionMap[context.direction] || 'bottom';
    const position2 = right ? 'end' : 'start';
    attributes.placement = `${position1}-${position2}`;
    attributes.component = tag;
    attributes.modifiers = !flip ? {
      ...modifiers,
      ...noFlipModifier,
    } : modifiers;
  }

  return (
    <Tag
      tabIndex="-1"
      role="menu"
      {...attributes}
      aria-hidden={!context.isOpen}
      className={classes}
      x-placement={attributes.placement}
      ref={innerRef}
    />
  );

}


CDropdownMenu.propTypes = {
  tag: tagPropType,
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  right: PropTypes.bool,
  flip: PropTypes.bool,
  modifiers: PropTypes.object,
  persist: PropTypes.bool,
};

CDropdownMenu.defaultProps = {
  tag: 'div',
  flip: true,
};

export default CDropdownMenu;

import React, {useContext} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {tagPropType, mapToCssModules} from './Shared/helper.js';
import {Target} from 'react-popper';
import CButton from './CButton';
import {Context} from './CDropdownCustom';

//component - CoreUI / CDropdownToggle

const CDropdownToggle = props=>{

  const {
    className,
    color,
    cssModule,
    //
    innerRef,
    caret,
    split,
    nav,
    header,
    tag,
    togglerHtml,
    ...attributes
  } = props;
  
  const context = useContext(Context);

  const onClick = e=>{
    if (props.disabled) {
      e.preventDefault();
      return;
    }
    if (props.nav && !props.tag) {
      e.preventDefault();
    }
    if (props.onClick) {
      props.onClick(e);
    }
    context.toggle(e);
  }

  //render

  const ariaLabel = attributes['aria-label'] || 'Toggle Dropdown';

  const classes = mapToCssModules(classNames(
    className,
    {
      'dropdown-toggle': caret || split,
      'dropdown-toggle-split': split,
      'nav-link': nav && !header,
      'c-header-nav-link': nav && header
    }
  ), cssModule);

  const children = togglerHtml || attributes.children || <span className="sr-only">{ariaLabel}</span>;

  let Tag;
  let isButton = false;

  if (nav  && !tag) {
    Tag = 'a';
    attributes.href = '#';
  } else if (!tag) {
    Tag = CButton;
    attributes.color = color;
    attributes.cssModule = cssModule;
    isButton = true;
  } else {
    Tag = tag;
  }

  if (context.inNavbar) {
    if (isButton)
      return (
        <Tag
          {...attributes}
          className={classes}
          onClick={onClick}
          aria-expanded={context.isOpen}
        >
          {children}
        </Tag>
      );
    else
      return (
        <Tag
          {...attributes}
          className={classes}
          onClick={onClick}
          aria-expanded={context.isOpen}
          ref={innerRef}
        >
          {children}
        </Tag>
      );
  }

  return (
    <Target
      {...attributes}
      className={classes}
      component={Tag}
      onClick={onClick}
      aria-expanded={context.isOpen}
      ref={innerRef}
    >
      {children}
    </Target>
  );

}

CDropdownToggle.propTypes = {
  tag: tagPropType,
  children: PropTypes.node,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  caret: PropTypes.bool,
  color: PropTypes.string,
  disabled: PropTypes.bool,
  onClick: PropTypes.func,
  'aria-haspopup': PropTypes.bool,
  split: PropTypes.bool,
  togglerHtml: PropTypes.node,
  nav: PropTypes.bool,
  header: PropTypes.bool,
};

CDropdownToggle.defaultProps = {
  'aria-haspopup': true
};

export default CDropdownToggle;

import React, {useState} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import CTooltipPopoverWrapper from './CTooltipPopoverWrapper';

//component - CoreUI / CPopover

const CPopover = props=>{

  let {
    custom,
    //
    toggle,
    show,
    defaultOpen,
    ...attributes
  } = props;

  const [isOpen, setIsOpen] = useState(defaultOpen || false);

  if (!custom){
    const userToggle = toggle;
    toggle = (e)=>{
      setIsOpen(!isOpen);
      if (userToggle)
        userToggle(e);
    }
    show = isOpen;
  }

  //render

  const popperClasses = classNames(
    'popover',
    'show',
    props.className
  );

  const classes = classNames(
    'popover-inner',
    props.innerClassName
  );

  return (
    <CTooltipPopoverWrapper
      {...attributes}
      toggle={toggle}
      show={show}
      className={popperClasses}
      innerClassName={classes}
    />
  );

}

CPopover.propTypes = {
  ...CTooltipPopoverWrapper.propTypes,
  custom: PropTypes.bool,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  defaultOpen: PropTypes.bool,
}

CPopover.defaultProps = {
  placement: 'right',
  placementPrefix: 'bs-popover',
  trigger: 'click',
};

