import React, {useContext} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {mapToCssModules, omit, tagPropType} from './Shared/helper.js';
import {Context} from './CDropdownCustom';

//component - CoreUI / CDropdownItem

const CDropdownItem = props=>{

  let {
    tag: Tag,
    className,
    cssModule,
    //
    innerRef,
    color,
    divider,
    header,
    active,
    ...attributes
  } = omit(props, ['toggle']);

  const context = useContext(Context);

  const onClick = e=>{
    if (props.disabled || props.header || props.divider) {
      e.preventDefault();
      return;
    }
    if (props.onClick) {
      props.onClick(e);
    }
    if (props.toggle) {
      context.toggle(e);
    }
  }

  const getTabIndex = ()=>{
    if (props.disabled || props.header || props.divider) {
      return '-1';
    }
    return '0';
  }

  //render

  const tabIndex = getTabIndex();
  const role = tabIndex > -1 ? 'menuitem' : undefined;

  const classes = mapToCssModules(classNames(
    className,
    header ? 'dropdown-header' : divider ? 'dropdown-divider' : 'dropdown-item',
    active ? 'active' : null,
    color ? 'bg-'+color : null,
    {
      disabled: attributes.disabled,
    }
  ), cssModule);

  if (Tag === 'button') {
    if (header) {
      Tag = 'h6';
    } else if (divider) {
      Tag = 'div';
    } else if (props.href) {
      Tag = 'a';
    }
  }

  return (
    <Tag
      type={(Tag === 'button' && (attributes.onClick || props.toggle)) ? 'button' : undefined}
      {...attributes}
      tabIndex={tabIndex}
      role={role}
      className={classes}
      onClick={onClick}
      ref={innerRef}
    />
  );

}

CDropdownItem.propTypes = {
  tag: tagPropType,
  children: PropTypes.node,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  color: PropTypes.string,
  href: PropTypes.string,
  divider: PropTypes.bool,
  header: PropTypes.bool,
  active: PropTypes.bool,
  disabled: PropTypes.bool,
  onClick: PropTypes.func,
  toggle: PropTypes.bool
};

CDropdownItem.defaultProps = {
  tag: 'button',
  toggle: true
};

export default CDropdownItem;

import React, {useContext} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {tagPropType, mapToCssModules} from './Shared/helper.js';
import {Popper} from 'react-popper';
import {Context} from './CDropdownCustom';

//component - CoreUI / CDropdownMenu

const CDropdownMenu = props=>{

  const {
    className,
    children,
    cssModule,
    right,
    tag,
    flip,
    modifiers,
    persist,
    innerRef,
    ...attributes
  } = props;

  const noFlipModifier = { flip: { enabled: false } };

  const directionPositionMap = {
    up: 'top',
    left: 'left',
    right: 'right',
    down: 'bottom',
  };

  const context = useContext(Context);

  //render

  const classes = mapToCssModules(classNames(
    className,
    'dropdown-menu',
    {
      'dropdown-menu-right': right,
      'show': context.isOpen,
    }
  ), cssModule);

  let Tag = tag;
  let placement;
  let myModifiers = modifiers;

  if (persist || (context.isOpen && !context.inNavbar)) {
    Tag = Popper;
    const position1 = directionPositionMap[context.direction] || 'bottom';
    const position2 = right ? 'end' : 'start';
    placement = `${position1}-${position2}`;
    //attributes.component = tag;
    myModifiers = !flip ? {
      ...modifiers,
      ...noFlipModifier,
    } : modifiers;
  }

  /*
  x-placement={attributes.placement}
  //ref={innerRef}
  */
  return (
    <Tag
      placement = {placement}
      modifiers = {myModifiers}
    >
      {({ ref, style, placement, arrowProps }) => {
        //console.log(style, children);
        return (
          <div
            {...attributes}
            tabIndex="-1"
            role="menu"
            aria-hidden={!context.isOpen}
            className={classes}
            ref={innerRef}
            data-placement={placement}
          >
            {children}
          </div>
        )}
      }
    </Tag>
  );

}


CDropdownMenu.propTypes = {
  tag: tagPropType,
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  right: PropTypes.bool,
  flip: PropTypes.bool,
  modifiers: PropTypes.object,
  persist: PropTypes.bool,
};

CDropdownMenu.defaultProps = {
  tag: 'div',
  flip: true,
};

export default CDropdownMenu;

import React, {useContext} from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {tagPropType, mapToCssModules} from './Shared/helper.js';
import {Reference} from 'react-popper';
import CButton from './CButton';
import {Context} from './CDropdownCustom';

//component - CoreUI / CDropdownToggle

const CDropdownToggle = props=>{

  const {
    className,
    color,
    cssModule,
    //
    innerRef,
    caret,
    split,
    nav,
    header,
    tag,
    togglerHtml,
    ...attributes
  } = props;

  const context = useContext(Context);

  const onClick = e=>{
    if (props.disabled) {
      e.preventDefault();
      return;
    }
    if (props.nav && !props.tag) {
      e.preventDefault();
    }
    if (props.onClick) {
      props.onClick(e);
    }
    context.toggle(e);
  }

  //render

  const ariaLabel = attributes['aria-label'] || 'Toggle Dropdown';

  const classes = mapToCssModules(classNames(
    className,
    {
      'dropdown-toggle': caret || split,
      'dropdown-toggle-split': split,
      'nav-link': nav && !header,
      'c-header-nav-link': nav && header
    }
  ), cssModule);

  const children = togglerHtml || attributes.children || <span className="sr-only">{ariaLabel}</span>;

  let Tag;
  let isButton = false;

  if (nav  && !tag) {
    Tag = 'a';
    attributes.href = '#';
  } else if (!tag) {
    Tag = CButton;
    attributes.color = color;
    attributes.cssModule = cssModule;
    isButton = true;
  } else {
    Tag = tag;
  }

  if (context.inNavbar) {
    if (isButton)
      return (
        <Tag
          {...attributes}
          className={classes}
          onClick={onClick}
          aria-expanded={context.isOpen}
        >
          {children}
        </Tag>
      );
    else
      return (
        <Tag
          {...attributes}
          className={classes}
          onClick={onClick}
          aria-expanded={context.isOpen}
          ref={innerRef}
        >
          {children}
        </Tag>
      );
  }

  return (
    <Reference>
      {({ ref }) => (
        <Tag
          ref={ref}
          {...attributes}
          className={classes}
          onClick={onClick}
          aria-expanded={context.isOpen}
          ref={innerRef}
        >
          {children}
        </Tag>
      )}
    </Reference>
  );
}

CDropdownToggle.propTypes = {
  tag: tagPropType,
  children: PropTypes.node,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  caret: PropTypes.bool,
  color: PropTypes.string,
  disabled: PropTypes.bool,
  onClick: PropTypes.func,
  'aria-haspopup': PropTypes.bool,
  split: PropTypes.bool,
  togglerHtml: PropTypes.node,
  nav: PropTypes.bool,
  header: PropTypes.bool,
};

CDropdownToggle.defaultProps = {
  'aria-haspopup': true
};

export default CDropdownToggle;

