    shape,
    align,
    alt,
    src,
    width,
    height,
    placeholderColor,
    active,
    fluidGrow,
    ...attributes
  } = props;

  // vars

  const isActive = (()=>{
      //active prop is present in CImgLazy component, here always undefined
      return active !== false
  })();

  const alignClass = (()=>{
    if (align === 'center') {
      return 'mx-auto'
    } else if (align === 'right') {
      return 'float-right'
