      return 'float-left'
    }
    return null
  })();

  // render

  const classes = mapToCssModules(classNames(
    className,
    alignClass,
    thumbnail ? 'img-thumbnail' : null,
    fluid || fluidGrow ? 'img-fluid' : null,
    fluidGrow ? 'w-100' : null,
    block ? 'd-block' : null,
    shape ? shape : null,
  ), cssModule);

  return (
    src && isActive?
      <img
        {...attributes}
        className={classes}
        src={src}
        alt={alt}
        width={width}
        height={height}
        ref={innerRef} /> :
      <svg
        {...attributes}
        className={classes}
        width={width}
        height={height}
        style={{'backgroundColor': isActive ? placeholderColor : 'transparent' }}
        ref={innerRef}
      />
  );

}

