import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {tagPropType, mapToCssModules, deprecated} from './Shared/helper.js';
import isobject from 'lodash.isobject';

const colWidths = ['xs', 'sm', 'md', 'lg', 'xl'];

const stringOrNumberProp = PropTypes.oneOfType([PropTypes.number, PropTypes.string]);

const columnProps = PropTypes.oneOfType([
  PropTypes.string,
  PropTypes.number,
  PropTypes.shape({
    size: stringOrNumberProp,
    push: deprecated(stringOrNumberProp, 'Please use the prop "order"'),
    pull: deprecated(stringOrNumberProp, 'Please use the prop "order"'),
    order: stringOrNumberProp,
    offset: stringOrNumberProp,
  }),
]);

const getColumnSizeClass = (isXs, colWidth, colSize)=>{
  if (colSize === true || colSize === '') {
    return isXs ? 'col' : `col-${colWidth}`;
  } else if (colSize === 'auto') {
    return isXs ? 'col-auto' : `col-${colWidth}-auto`;
  }
  return isXs ? `col-${colSize}` : `col-${colWidth}-${colSize}`;
};

//component - CoreUI / CLabel

const CLabel = props=>{

  const {
    tag: Tag,
    className,
    cssModule,
    //
    innerRef,
    hidden,
    widths,
    check,
    size,
    for: htmlFor,
    ...attributes
  } = props;

  // render

  const colClasses = [];

  widths.forEach((colWidth, i) => {

    let columnProp = props[colWidth];
    delete attributes[colWidth];

    if (!columnProp && columnProp !== '') {
      return;
    }

    const isXs = !i;
    let colClass;

    if (isobject(columnProp)) {
      const colSizeInterfix = isXs ? '-' : `-${colWidth}-`;
      colClass = getColumnSizeClass(isXs, colWidth, columnProp.size);
      colClasses.push(mapToCssModules(classNames({
        [colClass]: columnProp.size || columnProp.size === '',
        [`order${colSizeInterfix}${columnProp.order}`]: columnProp.order || columnProp.order === 0,
        [`offset${colSizeInterfix}${columnProp.offset}`]: columnProp.offset || columnProp.offset === 0
      })), cssModule);
    } else {
      colClass = getColumnSizeClass(isXs, colWidth, columnProp);
      colClasses.push(colClass);
    }

  });

  const classes = mapToCssModules(classNames(
    className,
    hidden ? 'sr-only' : false,
    check ? 'form-check-label' : false,
    size ? `col-form-label-${size}` : false,
    colClasses,
    colClasses.length ? 'col-form-label' : false
  ), cssModule);

  return (
    <Tag htmlFor={htmlFor} {...attributes} className={classes} ref={innerRef} />
  );

}

CLabel.propTypes = {
  tag: tagPropType,
  children: PropTypes.node,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
  hidden: PropTypes.bool,
  check: PropTypes.bool,
  size: PropTypes.string,
  for: PropTypes.string,
  xs: columnProps,
  sm: columnProps,
  md: columnProps,
  lg: columnProps,
  xl: columnProps,
  widths: PropTypes.array
};

CLabel.defaultProps = {
  tag: 'label',
  widths: colWidths
};

export default CLabel;

import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {mapToCssModules} from './Shared/helper.js';
import {NavLink} from 'react-router-dom';

//component - CoreUI / CLink

const CLink = props=>{

  const {
    className,
    cssModule,
    //
    innerRef,
    active,
    disabled,
    href,
    to,
    ...attributes
  } = props;

  let href2 = href;

  const onClick = e=>{
    if (props.disabled) {
      e.preventDefault();
      return;
    }
    if (href2 === '#') {
      e.preventDefault();
    }
    if (props.onClick) {
      props.onClick(e);
    }
  }

  // render

  const classes = mapToCssModules(classNames(
    className,
    active ? 'active' : null,
    disabled ? 'disabled' : null
  ), cssModule);

  //<span {...attributes} className={classes} ref={innerRef} />

  to || (href2 || (href2='#'));

  return (
    to ?
      <NavLink to={to} {...attributes} className={classes} ref={innerRef} /> :
        <a href={href2} {...attributes} className={classes} onClick={onClick} ref={innerRef} />
  );

}

CLink.propTypes = {
  className: PropTypes.string,
  cssModule: PropTypes.object,
  //
  innerRef: PropTypes.oneOfType([PropTypes.object, PropTypes.func, PropTypes.string]),
