  onChange: PropTypes.func

  //label: PropTypes.bool,
  /*
  outline: PropTypes.oneOfType([
    PropTypes.bool,
    PropTypes.string,
    PropTypes.oneOf(['', 'alt'])
  ]),
  */

};

