          </a>
        </CCardFooter>
      );
    }
  };

  return (
    <CCard {...attributes} custom>
      <CCardBody className={card.classes} {...attributes}>
        <div>
          {blockIcon(card.icon)}
        </div>
        <div>
          <div className={lead.classes}>{header}</div>
          <div className="text-muted text-uppercase font-weight-bold small">{mainText}</div>
        </div>
      </CCardBody>
      {cardFooter()}
    </CCard>
  );

}

